targets.rawSql:
SELECT $__timeGroup(ts, $__interval) AS "time", 
       category AS metric, count(*) AS value 
FROM ${log}
WHERE $__timeFilter(ts)
GROUP BY 1, 2
ORDER BY 1, 2;

SELECT app, count(*) AS count FROM ${log}
WHERE $__timeFilter(ts) AND app IS NOT NULL
GROUP BY app ORDER BY 1;

SELECT $__timeGroup(ts, $__interval) AS "time", 
       category AS metric, count(*) AS value 
FROM ${log}
WHERE $__timeFilter(ts) AND app = '$app'
GROUP BY 1, 2
ORDER BY 1, 2;
