targets.rawSql:
SELECT replace(string_agg(d, ',') , '${admin_ip}', pigsty.get_param('admin_ip'))
FROM pigsty.global_config v, jsonb_array_elements_text(v.value) d
WHERE v.key = 'node_repo_local_urls';

SELECT replace(record, '${admin_ip}', pigsty.get_param('admin_ip'))
FROM pigsty.global_config v, jsonb_array_elements_text(v.value) record
WHERE v.key = 'dns_records';
