targets.rawSql:
SELECT $__timeGroup(ts, $__interval) AS "time", 
       level AS metric, count(*) AS value 
FROM ${log}
WHERE $__timeFilter(ts)
GROUP BY 1, 2
ORDER BY 1, 2;

SELECT $__timeGroup(ts, $__interval) AS "time", 
       code AS metric, count(*) AS value 
FROM ${log}
WHERE $__timeFilter(ts)
GROUP BY 1, 2
ORDER BY 1, 2;

SELECT $__timeGroup(ts, $__interval) AS "time", 
       cmd_tag AS metric, count(*) AS value 
FROM ${log}
WHERE $__timeFilter(ts)
GROUP BY 1, 2
ORDER BY 1, 2;

SELECT $__timeGroup(ts, $__interval) AS "time", 
       coalesce(datname, '<null>') AS metric, count(*) AS value 
FROM ${log}
WHERE $__timeFilter(ts)
GROUP BY 1, 2
ORDER BY 1, 2;

SELECT $__timeGroup(ts, $__interval) AS "time", 
       coalesce(username, '<null>') AS metric, count(*) AS value 
FROM ${log}
WHERE $__timeFilter(ts)
GROUP BY 1, 2
ORDER BY 1, 2;

SELECT conn, count(*)
FROM ${log}
WHERE $__timeFilter(ts)
GROUP BY conn ORDER BY 1;

SELECT appname AS app, count(*)
FROM ${log}
WHERE $__timeFilter(ts)
GROUP BY appname ORDER BY 1;
